import ParametrosListItem from "./ParametrosListItem";
export default {
  props: {
    parametros: {
      type: Array
    },
    serviciosFilter: {
      type: Array
    },
    query: {
      type: Object
    }
  },
  methods: {
    searchByParametro(anArray, query) {
      if (query.query) {
        return anArray.filter(param => {
          return param.nombre.toLowerCase().includes(query.query.toLowerCase());
        });
      }
      return anArray;
    },
    searchByDescripcion(anArray, query) {
      if (query.query) {
        return anArray.filter(param => {
          return param.descripcion
            .toLowerCase()
            .includes(query.query.toLowerCase());
        });
      }
      return anArray;
    },
    searchByPalabrasClave(anArray, query) {
      if (query.query) {
        return anArray.filter(param => {
          //update: buscar por cada palabra clave
          return param.idRespuesta.idRuta.palabrasClave.includes(
            query.query.toLowerCase()
          );
        });
      }
      return anArray;
    }
  },
  computed: {
    parametrosList() {
      //hacer un smart filtrador
      if (this.serviciosFilter.length > 0) {
        let parametrosFiltered = this.parametros.filter(parametro => {
          return parametro.idRespuesta.idRuta.idServicio
            ? this.serviciosFilter.includes(
                parametro.idRespuesta.idRuta.idServicio._id
              )
            : false;
        });
        if (this.query.query) {
          switch (this.query.searchBy) {
            case 1:
              parametrosFiltered = this.searchByParametro(
                parametrosFiltered,
                this.query
              );
              break;
            case 2:
              parametrosFiltered = this.searchByDescripcion(
                parametrosFiltered,
                this.query
              );
              break;
            case 3:
              parametrosFiltered = this.searchByPalabrasClave(
                parametrosFiltered,
                this.query
              );
              break;
          }
        }
        return parametrosFiltered;
      } else {
        let parametrosAor = this.parametros;
        let parametrosFiltered = parametrosAor.sort((item, next) => {
          return (
            item.nombre.toString().toLowerCase() >
            next.nombre.toString().toLowerCase()
          );
        });
        if (this.query.query) {
          switch (this.query.searchBy) {
            case 1:
              parametrosFiltered = this.searchByParametro(
                parametrosFiltered,
                this.query
              );
              break;
            case 2:
              parametrosFiltered = this.searchByDescripcion(
                parametrosFiltered,
                this.query
              );
              break;
            case 3:
              parametrosFiltered = this.searchByPalabrasClave(
                parametrosFiltered,
                this.query
              );
              break;
          }
        }
        return parametrosFiltered;
      }
    }
  },
  components: {
    ParametrosListItem
  }
};
